mapper.insert:
INSERT  INTO
  lt_dictionariestype (
    key_name,
    rank,update_name_id,
   update_time,value_name
  )VALUES (
    #{keyName},
    #{rank},#{updateNameId},
    now(),#{valueName}
  )

mapper.select:
SELECT id,key_name, rank,update_name_id, update_time,value_name FROM lt_dictionariestype
key_name=#{keyName}
ORDER BY rank
