mapper.select:
select
gr.id,gr.user_id,gr.gift_id,gr.gr_releasetime,gr.create_time,
lg.title,lg.value,
lu.nickname,lu.mobile
from
lt_giftrecord AS gr
LEFT JOIN lt_gift AS lg ON gr.gift_id=lg.id
LEFT JOIN lt_user AS lu ON lu.id=gr.user_id

     AND gr.`user_id`=#{giftRecordParam.userId}
     AND gr.`gift_id`=#{giftRecordParam.giftId}
     AND gr.`gr_releasetime`=#{giftRecordParam.grReleasetime}
     AND gr.`create_time`=#{giftRecordParam.createTime}
     AND lg.title LIKE '%${giftRecordParam.title}%' 
     AND lg.`value`=#{giftRecordParam.value}
     AND lu.`nickname`=#{giftRecordParam.nickname}
     AND lu.`mobile`=#{giftRecordParam.mobile}

order by gr.create_time desc
